import React from 'react'
import '../Pages/styles.css'
import '../App.css'

const Confirmation = (props) => {

  const today = new Date();
  var options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };

    async function bookAppointment(e) {

        e.preventDefault()
        try {
          const response = await fetch('http://localhost:3001/api/book', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json'
            },
            body: JSON.stringify({
              date: props.selectedDay,
              service: props.service,
              userName: props.user.name,
              userEmail: props.user.email,
              userPhone: props.userPhone,
              userAddress: props.userAddress,
              userNotes: props.userNotes,
            })
          })
          const data = await response.json();
          processAppointment(data)
        } catch(err) {
          console.log(err)
        }
    }

    function backStep() {
      props.setFormStep(3);
    }

    function processAppointment(data) {
      console.log(data)
    }

    return (
        <div className="card">
        <div className="authBox">
            <h1>Confirmation</h1>
            <div>
                <div>
                  <h4>Time</h4>
                  <p>{props.selectedDay.toLocaleDateString('en-GB', options)}</p>
                </div>
                <div className="apptButtons">
                <input className="btn-primary" type="submit" value="Continue" onClick={bookAppointment}/>
                <input className="btn-secondary" type="submit" value="Back" onClick={backStep}/>
                </div>
            </div>

            </div>
        </div>
    )
}

export default Confirmation
