import React, {useState} from 'react';
import DayPicker from 'react-day-picker';
import 'react-day-picker/lib/style.css';

export default function Scheduler(props) {
  const [selectedDay, setSelectedDay] = useState('')
  const [service, setService] = useState('')

  const today = new Date();
  var options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };


  function handleDayClick(day, { selected }) {
    setSelectedDay(selected ? undefined : day)
  }

  async function bookAppointment() {

    try {
      const response = await fetch('http://localhost:3001/api/book', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          date: selectedDay,
          service,
          userName: props.user.name,
          userEmail: props.user.email,
          userPhone: props.user.userPhone,
          userAddress: props.user.userAddress,
          userNotes: props.user.userNotes,
        })
      })

      const data = await response.json();

    } catch(err) {
      console.log(err)
    }
  }

  function handleRadio(e) {
    setService(e.target.value)
  }

    return (
      <div>
        <DayPicker 
          showOutsideDays
          selectedDays={selectedDay}
          onDayClick={handleDayClick}
          disabledDays={{ before: today }}
        />
        <h5>
          {selectedDay
            ? selectedDay.toLocaleDateString('en-GB', options)
            : 'Choose a date on the calendar'}
        </h5>
        <input type="radio" id="water" name="services" value="Water supply service" onChange={handleRadio} />
        <label>Water supply services</label><br/>
        <input type="radio" id="tank" name="services" value="Tank cleaning service" onChange={handleRadio} />
        <label>Tank cleaning services</label><br/>
        <br />
        <input value={userPhone} onChange={(e) => setUserPhone(e.target.value)} type="text" placeholder="Phone" />
        <br />
        <input value={userAddress} onChange={(e) => setUserAddress(e.target.value)} type="text" placeholder="Address" />
        <br />
        <input value={userNotes} onChange={(e) => setUserNotes(e.target.value)} type="text" placeholder="Leave us a message" />
        <br />
        <input className="btn-primary" type="submit" value="Schedule appointment" onClick={bookAppointment}/>
      </div>
    );
}
