import React, { useState } from 'react'
import '../Pages/styles.css'
import '../App.css'

const Info = (props) => {

    async function bookAppointment(e) {

        try {
          const response = await fetch('http://localhost:3001/api/book', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json'
            },
            body: JSON.stringify({
              date: props.selectedDay,
              service: props.service,
              userName: props.user.name,
              userEmail: props.user.email,
              userPhone: props.userPhone,
              userAddress: props.userAddress,
              userNotes: props.userNotes,
            })
          })

          const data = await response.json();
        } catch(err) {
          console.log(err)
        }
    }

    function backStep() {
      props.setFormStep(2);
    }

    return (
        <div className="card">
        <div className="authBox">
            <h1>Info</h1>
            <div className="">

                <br />
                <input value={props.userPhone} onChange={(e) => props.setUserPhone(e.target.value)} type="text" placeholder="Phone" />
                <br />
                <input value={props.userAddress} onChange={(e) => props.setUserAddress(e.target.value)} type="text" placeholder="Address" />
                <br />
                <input value={props.userNotes} onChange={(e) => props.setUserNotes(e.target.value)} type="text" placeholder="Leave us a message" />
                <br />

                <div className="apptButtons">
                <input className="btn-primary" type="submit" value="Continue" onClick={bookAppointment}/>
                <input className="btn-secondary" type="submit" value="Back" onClick={backStep}/>
                </div>
            </div>

            </div>
        </div>
    )
}

export default Info
