import React, { useState, useEffect } from 'react'
import './styles.css'
import '../App.css'
import Footer from '../Components/Footer'
import logo from '../images/MB_Logo_alt.svg'

function Bookings() {
    const [appointments, setAppointments] = useState([])
    var options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };

    useEffect(() => {
      fetch('http://localhost:3001/api/bookings', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            token: localStorage.getItem('token')
          })
        })
      .then(data => data.json())
      .then(data => data.error ? window.location = '/register' : setAppointments(data) )
      .then(data => console.log(data))
      }, [])

      async function cancelAppointment(e) {

          e.preventDefault()
          try {
            const response = await fetch('http://localhost:3001/api/book', {
              method: 'POST',
              headers: {
                'Content-Type': 'application/json'
              },
              body: JSON.stringify({body: ''
              })
            })
            const data = await response.json();
          } catch(err) {
            console.log(err)
          }
      }

    return (
        <div>
          <div className="servicesCard">
            <div className="servicesWrapper">
            <h1>Upcoming Appointments</h1><br />
                  <div className="appointmentsWrapper">
                {appointments.length >= 1 ? appointments.map((appointment, i) => (
                <>
                  <br />
                  <div className="headerBanner">
                    <img alt="maratai blue logo" src={logo}/>
                    <h2>{appointment.service}</h2>
                  <h4>{appointment.date}</h4>
                  </div>
                  <div className="appointmentWrapper">
                      <div className="confirmationSection" style={{textAlign: 'Left'}}>
                        <p><b>Name:</b></p><p>{appointment.userName}</p>
                        <p><b>Phone:</b></p><p>{appointment.userPhone}</p>
                        <p><b>Address:</b></p><p>{appointment.userAddress}</p>
                        {appointment.userNotes ? <><p><b>Notes:</b></p><p>{appointment.userNotes}</p></> : <></> }
                      </div>
                <input className="btn-secondary" type="submit" value="Cancel" onClick={cancelAppointment}/>
                    </div>
                  </>
                )
                  ) : 
                  <h3>You have no upcoming appointments.</h3>  
                }
                </div>
            </div>
            </div>
            <Footer />
        </div>
    )
}

export default Bookings
