import React from 'react'

function Size(props) {

    function backStep() {
      props.setFormStep(0);
    }

    async function nextStep() {
      props.user ? props.setFormStep(2) : window.location = '/register'
    }

    return (
        <div className="authBox">
            <h1>Tank refill size</h1>
            <div className="servicesBox">
                <button className="btn-primary" type="submit" value="5k Litres" onClick={nextStep}>5k Litres</button>
                <button className="btn-primary" type="submit" value="10k Litres" onClick={nextStep}>10k Litres</button>
                <button className="btn-primary" type="submit" value="14k Litres" onClick={nextStep}>14k Litres</button>
            </div>
          <input className="btn-secondary" type="submit" value="Back" onClick={backStep}/>

        </div>
    )
}

export default Size
