import React, { useState, useEffect, useRef } from 'react';
import '../Pages/styles.css'
import '../App.css'

const Info = (props) => {
  const [err, setErr] = useState({})
  const [disable, setDisabled] = useState(true)

  const firstRender = useRef(true)

  useEffect(() => {

    if (firstRender.current) {
      firstRender.current = false
      return
    }

    setDisabled(formValidation())

  }, [name, email, password, password2])

  const formValidation = () => {
    let regEmail = /^[a-zA-Z0-9]+@[a-zA-Z0-9]+\.[A-Za-z]+$/;
    let regPassword = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)[A-Za-z\d#$@!%&*?]{8,30}$/;
    let newErrors = {}
    let err = false

    if (name === "") {
      newErrors.name = <><hr/>Name can't be blank</>
      err = true
    }
    if (!regEmail.test(email)) {
      newErrors.email = <><hr/>Enter a valid email address</>
      err = true
    }
    if (!regPassword.test(password)) {
      newErrors.password = <><hr/>Password must contain atleast: <br/>1 upper case letter, <br/>1 lower case letter, <br/>1 number, <br/>and be atleast 8 characters</>
      err = true
    }
    if (password !== password2) {
      newErrors.password = <><hr/>Passwords do not match</>
      err = true
    }
    setErr(newErrors)
    return err;
  }

    function backStep() {
      props.setFormStep(2);
    }

    function nextStep() {
      props.setFormStep(4);
    }

    return (
        <div className="card">
        <div className="authBox">
            <h1>Info</h1>
            <div className="infoBox">
                <br />
                <input value={props.userPhone} onChange={(e) => props.setUserPhone(e.target.value)} type="text" placeholder="Phone" />
                <br />
                <input value={props.userAddress} onChange={(e) => props.setUserAddress(e.target.value)} type="text" placeholder="Address" />
                <br />
                <textarea rows="5" value={props.userNotes} onChange={(e) => props.setUserNotes(e.target.value)} type="text" placeholder="Leave us a message" ></textarea>
                <br />
                <div className="apptButtons">
                <input className="btn-primary" type="submit" value="Continue" onClick={nextStep}/>
                <input className="btn-secondary" type="submit" value="Back" onClick={backStep}/>
                </div>
            </div>

            </div>
        </div>
    )
}

export default Info
