import React, { useState, useEffect } from 'react'
import './styles.css'
import '../App.css'
import Footer from '../Components/Footer'
import Scheduler from '../Components/Scheduler'

function Home() {
    const [user, setUser] = useState('')

    useEffect(() => {
      fetch('http://localhost:3001/api/home', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            token: localStorage.getItem('token')
          })
        })
       .then(data => data.json())
       .then(data => data.error ? window.location = '/register' : setUser(data))
      }, [])

    async function handleBooking(e) {
        e.preventDefault()
    }

    return (
        <div className="card">
            <form className="authBox" onSubmit={handleBooking}>
                <h4>{user ? `Welcome ${user.name}, ` : ''}</h4>
                <h2>Book an appointment</h2>
                <Scheduler user={user} />
            </form>
            <Footer />
        </div>
    )
}

export default Home
