import React, { useState } from 'react'
import '../Pages/styles.css'
import '../App.css'

const Info = (props) => {
    const [userPhone, setUserPhone] = useState('')
    const [userAddress, setUserAddress] = useState('')
    const [userNotes, setUserNotes] = useState('')

    async function bookAppointment(e) {

        e.preventDefault()
        try {
          const response = await fetch('http://localhost:3001/api/book', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json'
            },
            body: JSON.stringify({
              date: selectedDay,
              service,
              userName: props.user.name,
              userEmail: props.user.email,
              userPhone: props.user.userPhone,
              userAddress: props.user.userAddress,
              userNotes: props.user.userNotes,
            })
          })

          const data = await response.json();

        } catch(err) {
          console.log(err)
        }
    }

    return (
        <div className="authBox">
            <h1>Info</h1>
            <div className="">

                <br />
                <input value={userPhone} onChange={(e) => setUserPhone(e.target.value)} type="text" placeholder="Phone" />
                <br />
                <input value={userAddress} onChange={(e) => setUserAddress(e.target.value)} type="text" placeholder="Address" />
                <br />
                <input value={userNotes} onChange={(e) => setUserNotes(e.target.value)} type="text" placeholder="Leave us a message" />
                <br />
                <input className="btn-primary" type="submit" value="Continue" onClick={bookAppointment}/>
            </div>

        </div>
    )
}

export default Info
