import React, {useState} from 'react';
import DayPicker from 'react-day-picker';
import 'react-day-picker/lib/style.css';

export default function Scheduler(props) {
  const [selectedDay, setSelectedDay] = useState('')
  const [service, setService] = useState('a')
  const [userPhone, setUserPhone] = useState('a')
  const [userAddress, setUserAddress] = useState('a')
  const [userNotes, setUserNotes] = useState('')

  const today = new Date();
  var options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };

  async function bookAppointment(day, {selected}) {
    setSelectedDay(
      selected ? undefined : day,
    );

    console.log(day)
    console.log(props.user)

    const response = await fetch('http://localhost:3001/api/book', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        date: day,
        service,
        userName: props.user.name,
        userEmail: props.user.email,
        userPhone,
        userAddress,
        userNotes,
      })
    })

    const data = await response.json();
    console.log(data)
  }

  function handleRadio(e) {
    setService(e.target.value)
  }

    return (
      <div>
        <DayPicker 
          showOutsideDays
          selectedDays={selectedDay}
          onDayClick={setSelectedDay}
          disabledDays={{ before: today }}
        />
        <h5>
          {selectedDay
            ? selectedDay.toLocaleDateString('en-GB', options)
            : 'Choose a date on the calendar'}
        </h5>
        <input type="radio" id="html" name="services" value="Water supply service" onChange={handleRadio} />
        <label for="html">Water supply services</label><br/>
        <input type="radio" id="html" name="services" value="Tank cleaning service" onChange={handleRadio} />
        <label for="html">Tank cleaning services</label><br/>
        <br />
        <input value={userNotes} onChange={(e) => setUserNotes(e.target.value)} type="text" placeholder="Leave us a message" />
        <br />
        <input className="btn-primary" type="submit" value="Schedule appointment" onClick={bookAppointment}/>

      </div>
    );
}
