import React, { useState } from 'react'
import './styles.css'
import '../App.css'
import Footer from '../Components/Footer'
import Scheduler from '../Components/Scheduler'

function Home() {
    const [user, setUser] = useState('')

    if (localStorage.getItem('token')) getUser()

    async function getUser() {

      console.log('get user')

        const response = await fetch('http://localhost:3001/api/home', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            token: localStorage.getItem('token')
          })
        })

        const data = await response.json();

        if(data) setUser(data.name)
    }

    async function handleBooking(e) {
        e.preventDefault()
        user ? 
        console.log('appointment booked') 
        : window.location = '/register'
    }

    return (
        <div className="card">
            <form className="authBox" onSubmit={handleBooking}>
                <h4>{user ? `Welcome ${user}, ` : ''}</h4>
                <h2>Book an appointment</h2>
                <Scheduler />
                <input type="radio" id="html" name="services" value="Water" />
                <label for="html">Water supply services</label><br/>
                <input type="radio" id="html" name="services" value="Tank" />
                <label for="html">Tank cleaning services</label><br/>
                <input className="btn-primary" type="submit" value="Schedule appointment"/>
            </form>
            <Footer />
        </div>
    )
}

export default Home
