import React, { useState } from 'react'
import './styles.css'
import '../App.css'
import Footer from '../Components/Footer'
import Scheduler from '../Components/Scheduler'

function Home() {
    const [user, setUser] = useState('')

    if (localStorage.getItem('token')) getUser()

    async function getUser() {

        const response = await fetch('http://localhost:3001/api/home', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            token: localStorage.getItem('token')
          })
        })

        const data = await response.json();

        if(data) setUser(data.name)
    }

    async function HandleBooking() {
        console.log('appointment booked')
    }

    return (
        <div className="home">
            <form className="authBox" onSubmit={handleBooking}>
                <h4>{user ? `Welcome ${user}, ` : ''}</h4>
                <h2>Book an appointment</h2>
                <Scheduler />
                <input className="btn-primary" type="submit" value="Login"/>
            </form>
            <Footer />
        </div>
    )
}

export default Home
