import React, { useState, useEffect } from 'react'
import './styles.css'
import '../App.css'
import Footer from '../Components/Footer'

function Bookings() {
    const [appointments, setAppointments] = useState([])

    useEffect(() => {
      fetch('http://localhost:3001/api/bookings', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            token: localStorage.getItem('token')
          })
        })
      .then(data => data.json())
      .then(data => data.error ? window.location = '/register' : setAppointments(data) )
      .then(data => console.log(data))
      }, [])

    return (
        <div>
          <div className="servicesCard">
            <div className="servicesWrapper">
            <h1>Upcoming Appointments</h1><br />
                  <div className="appointmentWrapper">
                {appointments.length >= 1 ? appointments.map((appointment, i) => (
                    <div key={i}><div className="appointmentSection">
                      <h4>{appointment.service}</h4>
                      <p><b>{appointment.userAddress}</b></p>
                    </div>
                    <div className="appointmentSection">
                      <h6>{appointment.date ? `${appointment.date.slice(8,10)} ${appointment.date.slice(5,7)} ${appointment.date.slice(0,4)}` : ''}</h6>
                    </div>
                    <p>We will call <b>{appointment.userPhone}</b> 15 minutes prior to arrival.</p><hr />
                    </div>
                )
                  ) : 
                  <h3>You have no upcoming appointments.</h3>  
                }
                </div>
            </div>
            </div>
            <Footer />
        </div>
    )
}

export default Bookings
