import React, { useState } from 'react'
import './styles.css'
import '../App.css'
import { FaPhone } from 'react-icons/fa'
import Scheduler from '../Components/Scheduler'

function Home() {
    const [user, setUser] = useState('')

    if (localStorage.getItem('token')) getUser()

    async function getUser() {

        const response = await fetch('http://localhost:3001/api/home', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            token: localStorage.getItem('token')
          })
        })

        const data = await response.json();

        if(data) setUser(data.name)
    }

    return (
        <div className="home">
            <div className="home-booking">
                <h4>{user ? `Welcome ${user}, ` : ''}</h4>
                <h2>Book an appointment</h2>
                <Scheduler />
            </div>
            <div id="home">
                <h1>Maraetai Blue Limited</h1>
                <div className="services">
                    <div>
                        <h5>Water supply services</h5>
                        <p><FaPhone /> 09 536 6437</p>
                    </div>
                    <div>
                        <h5>Tank cleaning Services</h5>
                        <p><FaPhone /> 021 0298 4170</p>
                </div>
                </div>
            </div>
        </div>
    )
}

export default Home
