import React from 'react'
import './styles.css'
import '../images/animations.css'
import '../App.css'
import logo from '../images/MB_Logo.svg'

export default function Contact() {
    const [message, setMessage] = useState('')
    const [from, setFrom] = useState('')

    async function sendMessage(event) {
      event.preventDefault()
      const response = await fetch(`${process.env.REACT_APP_APIURL}/api/contact`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
            message, 
            from,
        })
      })

      const data = await response.json();

      if (data) {
        window.location = data.redirect
      }
    }

    return (
        <div>
            <div className="pageWrapper">
                <div className="pageText">
                    <h1>Contact us</h1>
                </div>
                <div className="pageBanner">
                </div>
            </div>
            <div className="servicesCard" id="fill">
                <div className="servicesWrapper">
                    <form onSubmit={sendMessage}>
                    <input value={from} onChange={(e) => setFrom(e.target.value)} type="email" placeholder="Email" />
                    <br />
                    <input value={password} onChange={(e) => setPassword(e.target.value)} type="password" placeholder="Password" />
                    <br />
                    <input className="btn-primary" type="submit" value="Login"/>
                    <p>
                    <a href="/">Forgot password</a>
                    </p>
                    </form>
                </div>
            </div>
        </div>
    )
}
