import React, {  useEffect, useState } from 'react'

const Profile = () => {
    const [user, setUser] = useState('')
    const [userPhone, setUserPhone] = useState('')
    const [userAddress, setUserAddress] = useState('')

    useEffect(() => {
      fetch(`${process.env.REACT_APP_APIURL}/api/home`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            token: localStorage.getItem('token')
          })
        })
       .then(data => data.json())
       .then(data => data.error ? console.log('error logging in') : login(data))
    }, [])

    function login(user) {
      setUser(user)
      setUserPhone(user.phone)
      setUserAddress(user.address)
    }

    return (
      <div className="card">
        <div className="authBox">
            <h1>Info</h1>
            <div className="infoBox">
                <br />
                <input value={userPhone} onChange={(e) => setUserPhone(e.target.value)} type="text" placeholder='Phone' />
                <input value={userAddress} onChange={(e) => setUserAddress(e.target.value)} type="text" placeholder="Address" />
