import React, { useState, useEffect } from 'react'
import './styles.css'
import '../App.css'
import Footer from '../Components/Footer'

function Bookings() {
    const [appointments, setAppointments] = useState(["empty"])

    useEffect(() => {
      fetch('http://localhost:3001/api/bookings', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            token: localStorage.getItem('token')
          })
        })
      .then(data => data.json())
      .then(data => setAppointments(data))
      .then()
      }, [generateAppointments()])

    function generateAppointments() {
      return (<>
        {appointments !== ["empty"] ? appointments.map((appointment) => (
          <div key={appointment.id} className="appointmentWrapper">
            <div className="appointmentSection">
              <h4>{appointment.service}</h4>
              <p><b>{appointment.userAddress}</b></p>
            </div>
            <div className="appointmentSection">
              <h4>{`${appointment.date.slice(8,10)} ${appointment.date.slice(5,7)} ${appointment.date.slice(0,4)}`}</h4>
            </div>
            <p>We will call <b>{appointment.userPhone}</b> 15 minutes prior to arrival.</p>
          </div>
        )
          ) : 
          <h3>You have no upcoming appointments.</h3>  
        }</>
      )
    }

    return (
        <div className="card">
            <div className="authBox">
            <h1>Upcoming Appointments</h1><br />
            {generateAppointments}
            </div>
            <Footer />
        </div>
    )
}

export default Bookings
