import './styles.css'
import '../App.css'
import React, { useState } from 'react';
import {Link} from 'react-router-dom'

function Register() {
  const [name, setName] = useState('')
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [password2, setPassword2] = useState('')
  const [err, setErr] = useState('')

  async function registerUser(event) {
    event.preventDefault()
    if (password === password2) {
        const response = await fetch('http://localhost:3001/api/register', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            name, 
            email, 
            password
          })
        })

        const data = await response.json();
        console.log(data.status)

        data.status === 'error' ? setErr(data.err) : window.location = data.redirect;
    }
    else setErr('Passwords do not match.')
  }

  return (
    <div className="page">
      <div className="authBox" id="register">
        <h1>Register</h1>
        <form onSubmit={registerUser}>
          <input value={name} onChange={(e) => setName(e.target.value)} type="text" placeholder="Name" />
          <br />
          <input value={email} onChange={(e) => setEmail(e.target.value)} type="email" placeholder="Email" />
          <br />
          <input value={password} onChange={(e) => setPassword(e.target.value)} type="password" placeholder="Password" />
          <br />
          <input value={password2} onChange={(e) => setPassword2(e.target.value)} type="password" placeholder="Confirm Password" />
          <br />
          <input className="btn-primary" type="submit" value="Register"/>
        </form>
          <p>Already have an account? <Link to="/Login">Login</Link></p>
          <br />
          <p style={{color: 'red'}}>{err}</p>
      </div>
    </div>
  );
}

export default Register;
